import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Header } from "../../components/SupportTickets/Header";
import { ChatWidget } from "../../components/SupportTickets/ChatWidget";

function SupportTickets() {
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [lastFocusedElement, setLastFocusedElement] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState("All Status");

  function toggleModal(ticket = null) {
    if (!showModal) {
      setLastFocusedElement(document.activeElement);
      setSelectedTicket(ticket);
      setShowModal(true);
    } else {
      setSelectedTicket(null);
      setShowModal(false);
      if (lastFocusedElement) {
        lastFocusedElement.focus();
      }
    }
  }

  function handleKeyDown(event) {
    if (event.key === "Escape") {
      toggleModal();
    }
  }

  useEffect(() => {
    if (showModal) {
      document.addEventListener("keydown", handleKeyDown);
      return () => {
        document.removeEventListener("keydown", handleKeyDown);
      };
    }
  }, [showModal]);

  return (
    <div className="w-full bg-pink-50 min-h-screen">
      <Header />
      <div className="px-9 py-12 max-sm:px-4 max-sm:py-6">
        <div className="flex justify-between items-center mb-8">
          <div className="text-3xl font-bold leading-10 text-zinc-800 max-sm:text-2xl">
            My Tickets
          </div>
          <Link to="/submit-ticket">
            <button className="px-6 py-3 text-sm font-medium text-white bg-pink-300 rounded-2xl shadow-sm">
              New Ticket
            </button>
          </Link>
        </div>

        <div className="p-8 bg-white rounded-3xl shadow-lg max-sm:p-4">
          <div className="flex gap-4 mb-8 max-sm:flex-col">
            <input
              type="text"
              placeholder="Search tickets..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="flex-1 px-4 text-sm rounded-xl border-2 border-pink-100 h-[49px] text-neutral-400"
            />
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="text-sm text-black rounded-xl bg-zinc-300 h-[49px] w-[131px] max-sm:w-full px-4"
            >
              <option>All Status</option>
              <option>Open</option>
              <option>In Progress</option>
              <option>Closed</option>
            </select>
          </div>

          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border border-pink-100">
                  <th className="py-4 text-sm font-medium text-left text-gray-500 px-4">
                    Ticket ID
                  </th>
                  <th className="py-4 text-sm font-medium text-left text-gray-500 px-4">
                    Subject
                  </th>
                  <th className="py-4 text-sm font-medium text-left text-gray-500 px-4">
                    Category
                  </th>
                  <th className="py-4 text-sm font-medium text-left text-gray-500 px-4">
                    Status
                  </th>
                  <th className="py-4 text-sm font-medium text-left text-gray-500 px-4">
                    Priority
                  </th>
                  <th className="py-4 text-sm font-medium text-left text-gray-500 px-4">
                    Last Update
                  </th>
                  <th className="py-4 text-sm font-medium text-left text-gray-500 px-4">
                    Action
                  </th>
                </tr>
              </thead>
              <tbody>
                <tr className="border border-pink-100">
                  <td className="py-6 text-sm text-zinc-800 px-4">
                    TK-2024-001
                  </td>
                  <td className="py-6 text-sm text-zinc-800 px-4">
                    Missing Item from Order #JC45692
                  </td>
                  <td className="py-6 px-4">
                    <div className="px-3 py-0.5 text-sm text-pink-300 bg-pink-50 rounded-lg inline-block">
                      Orders & Shipping
                    </div>
                  </td>
                  <td className="py-6 text-sm font-medium text-pink-300 px-4">
                    Open
                  </td>
                  <td className="py-6 text-sm text-red-500 px-4">High</td>
                  <td className="py-6 text-sm text-gray-500 px-4">
                    2 hours ago
                  </td>
                  <td className="py-6 px-4">
                    <button
                      className="px-4 py-2 text-sm text-pink-300 bg-pink-50 rounded-lg transition-all cursor-pointer duration-200 hover:bg-pink-100"
                      aria-controls="ticket-modal"
                      aria-expanded={showModal}
                      onClick={() =>
                        toggleModal({
                          id: "TK-2024-001",
                          subject: "Missing Item from Order #JC45692",
                          category: "Orders & Shipping",
                          status: "Open",
                          statusColor: "#FF9ECD",
                          priority: "High",
                          priorityColor: "#FF4B4B",
                          description:
                            "Order arrived but missing one Bashful Bunny Medium. Order was placed on January 10th and received January 15th.",
                          updates: [
                            {
                              message: "Ticket created",
                              time: "2024-01-15 09:15 AM",
                            },
                            {
                              message: "Support team reviewing order details",
                              time: "2024-01-15 10:30 AM",
                            },
                          ],
                        })
                      }
                    >
                      View Details
                    </button>
                  </td>
                </tr>
                <tr className="border border-pink-100">
                  <td className="py-6 text-sm text-zinc-800 px-4">
                    TK-2024-002
                  </td>
                  <td className="py-6 text-sm text-zinc-800 px-4">
                    Washing Instructions for Bashful Dragon
                  </td>
                  <td className="py-6 px-4">
                    <div className="px-3 py-0.5 text-sm text-pink-300 bg-pink-50 rounded-lg inline-block">
                      Product Care
                    </div>
                  </td>
                  <td className="py-6 text-sm font-medium text-emerald-500 px-4">
                    Closed
                  </td>
                  <td className="py-6 text-sm text-yellow-500 px-4">Normal</td>
                  <td className="py-6 text-sm text-gray-500 px-4">
                    3 days ago
                  </td>
                  <td className="py-6 px-4">
                    <button className="px-4 py-2 text-sm text-pink-300 bg-pink-50 rounded-lg hover:bg-pink-100">
                      View Details
                    </button>
                  </td>
                </tr>
                <tr className="border border-pink-100">
                  <td className="py-6 text-sm text-zinc-800 px-4">
                    TK-2024-003
                  </td>
                  <td className="py-6 text-sm text-zinc-800 px-4">
                    Return Request for Damaged Item
                  </td>
                  <td className="py-6 px-4">
                    <div className="px-3 py-0.5 text-sm text-pink-300 bg-pink-50 rounded-lg inline-block">
                      Returns
                    </div>
                  </td>
                  <td className="py-6 text-sm font-medium text-yellow-500 px-4">
                    In Progress
                  </td>
                  <td className="py-6 text-sm text-yellow-500 px-4">Normal</td>
                  <td className="py-6 text-sm text-gray-500 px-4">1 day ago</td>
                  <td className="py-6 px-4">
                    <button className="px-4 py-2 text-sm text-pink-300 bg-pink-50 rounded-lg hover:bg-pink-100">
                      View Details
                    </button>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {showModal && (
        <div
          role="presentation"
          className="flex fixed inset-0 justify-center items-center bg-black bg-opacity-50 z-[1000]"
          onClick={(event) => {
            if (event.target === event.currentTarget) {
              toggleModal();
            }
          }}
        >
          <div
            id="ticket-modal"
            role="dialog"
            aria-modal="true"
            aria-labelledby="modal-title"
            aria-describedby="modal-description"
            tabIndex="-1"
            className="overflow-auto relative p-8 bg-white rounded-3xl max-h-[90vh] max-w-[600px] w-[90%] max-sm:p-6 max-sm:w-[95%]"
            onKeyDown={(event) => {
              if (event.key === "Tab") {
                const focusableElements = event.currentTarget.querySelectorAll(
                  'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])',
                );
                const firstElement = focusableElements[0];
                const lastElement =
                  focusableElements[focusableElements.length - 1];

                if (event.shiftKey && document.activeElement === firstElement) {
                  event.preventDefault();
                  lastElement.focus();
                } else if (
                  !event.shiftKey &&
                  document.activeElement === lastElement
                ) {
                  event.preventDefault();
                  firstElement.focus();
                }
              }
            }}
          >
            <button
              className="absolute top-6 right-6 p-2 text-2xl text-gray-500 rounded cursor-pointer border-none hover:text-gray-700"
              aria-label="Close modal"
              onClick={() => toggleModal()}
            >
              Ã—
            </button>
            <h2
              id="modal-title"
              className="mb-6 text-2xl font-bold text-zinc-800"
            >
              {selectedTicket?.subject}
            </h2>
            <div className="grid gap-4 mb-6 grid-cols-[1fr_1fr] max-sm:grid-cols-[1fr]">
              <div>
                <div className="mb-1 text-gray-500">Ticket ID</div>
                <div className="font-medium text-zinc-800">
                  {selectedTicket?.id}
                </div>
              </div>
              <div>
                <div className="mb-1 text-gray-500">Category</div>
                <div className="inline-block px-3 py-1 text-pink-300 bg-pink-50 rounded-lg">
                  {selectedTicket?.category}
                </div>
              </div>
              <div>
                <div className="mb-1 text-gray-500">Status</div>
                <div style={{ color: selectedTicket?.statusColor }}>
                  {selectedTicket?.status}
                </div>
              </div>
              <div>
                <div className="mb-1 text-gray-500">Priority</div>
                <div style={{ color: selectedTicket?.priorityColor }}>
                  {selectedTicket?.priority}
                </div>
              </div>
            </div>
            <div className="mb-6">
              <div className="mb-2 text-gray-500">Description</div>
              <div
                id="modal-description"
                className="leading-normal text-zinc-800"
              >
                {selectedTicket?.description}
              </div>
            </div>
            <div>
              <div className="mb-4 text-gray-500">Ticket Updates</div>
              <div className="flex flex-col gap-3">
                {selectedTicket?.updates?.map((update, index) => (
                  <div className="flex gap-3 items-start" key={index}>
                    <div className="mt-1.5 w-2 h-2 bg-pink-300 rounded-full" />
                    <div>
                      <div className="text-zinc-800">{update.message}</div>
                      <div className="text-xs text-gray-500">{update.time}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}

      <ChatWidget maxMessageLength={500} maxMessages={50} />
    </div>
  );
}

export default SupportTickets;
