import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Header.css";

export const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <header className="header">
      <div className="header-container">
        <div className="header-brand">
          <Link to="/" className="brand-link">
            <div className="brand-logo" />
            <div className="brand-name">Jellycat Support</div>
          </Link>
        </div>

        {/* Mobile menu toggle */}
        <button 
          className="mobile-menu-toggle" 
          onClick={toggleMobileMenu}
          aria-expanded={isMobileMenuOpen}
          aria-label="Toggle menu"
        >
          <span className="toggle-icon">{isMobileMenuOpen ? '✕' : '☰'}</span>
        </button>

        {/* Navigation menu - will be shown/hidden on mobile based on state */}
        <nav className={`nav-menu ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
          <Link to="/dashboard" className="nav-item">Dashboard</Link>
          <Link to="/help-center" className="nav-item">Help Center</Link>
          <Link to="/support-tickets" className="nav-item">My Tickets</Link>
          <Link to="/submit-ticket" className="nav-item">Submit Ticket</Link>
        </nav>

        <div className="user-profile">
          <div className="avatar">JS</div>
          <div className="user-name">John Smith</div>
        </div>
      </div>
    </header>
  );
};
